'use strict'
let Functions = {
    getDateDiff : 
        function (date_1, date_2){
            const millis = date_1 - date_2;
            const diff = Math.floor(millis / 1000);
            //    console.log(\"Diff: \" + diff);
            return diff;
        },
    getActivityLevel : 
        function (lastReceiptTime, sleepDuration){
            //console.log("lastReceiptTime: " + lastReceiptTime );

            const millis = Date.now() - lastReceiptTime;

            //Diff est en secondes
            const diff = Math.floor(millis / 1000);
          //  console.log("Diff: " + diff + " - sleep: " + sleepDuration);

            if( diff > (sleepDuration * 2)){
                //La période de sleep est dépassée * 2. 
                // On mets au rouge.
                return 0;

            }else if( diff > (sleepDuration * 1.2)){
                //La période de sleep est dépassée * 1.2. 
                // On mets au jaune.
                return 50;
            }else{
                //On a reçu du data à l'intérieure de la période de sleep. 
                // On mets au vert.
                return 100;  
            }
        },
    padTo2Digits :
        function (num) {
          return num.toString().padStart(2, '0');
        },
    formatDate : 
        function (date) {
          return (
            [
             // date.getFullYear(),
              Functions.padTo2Digits(date.getMonth() + 1),
              Functions.padTo2Digits(date.getDate()),
            ].join('/') +
            ' ' +
            [
              Functions.padTo2Digits(date.getHours()),
              Functions.padTo2Digits(date.getMinutes()),
              Functions.padTo2Digits(date.getSeconds()),
            ].join(':')
          );
        }
    }

